INSERT INTO `bak_fpss_slides` (`id`, `title`, `catid`, `published`, `publish_up`, `publish_down`, `created`, `created_by`, `modified`, `modified_by`, `access`, `ordering`, `featured`, `featured_ordering`, `text`, `tagline`, `referenceType`, `referenceID`, `custom`, `video`, `hits`, `language`, `params`) VALUES
(1, 'Frontpage Slideshow', 1, 1, '2012-02-28 19:39:45', '0000-00-00 00:00:00', '2012-02-28 19:39:45', 62, '0000-00-00 00:00:00', 0, 0, 1, 0, 1, '\n        Frontpage SlideShow is the most eye-catching way to display your featured articles, stories or even products in your Joomla! based website.\n      ', 'Eye-candy for any Joomla! based website', '', 0, '', '', 14, '', 'title=\ntext=\ntagline=\ncategory=\nauthor=\nreadmore=\ntarget=\nliveData=\n\n'),
(2, 'Frontpage Slideshow is Search Engine Friendly!', 1, 1, '2012-02-28 19:39:45', '0000-00-00 00:00:00', '2012-02-28 19:39:45', 62, '0000-00-00 00:00:00', 0, 0, 2, 0, 1, '\n        Unlike Flash based slideshows, Frontpage Slideshow uses CSS and Javascript wizardry only. The content of the slides is laid out as html code, which means it can be crawled by search engines. The proper usage (and order) of h1, h2, p (and more) tags will make sure Google (or any other search engine) regularly indexes your latest/featured items.\n      ', '…And iPhone & iPad friendly too!', '', 0, '', '', 2, '', 'title=\ntext=\ntagline=\ncategory=\nauthor=\nreadmore=\ntarget=\nliveData=\n\n'),
(3, 'Unlimited slideshows, MVC templating', 1, 1, '2012-02-28 19:39:45', '0000-00-00 00:00:00', '2012-02-28 19:39:45', 62, '0000-00-00 00:00:00', 0, 0, 3, 0, 1, '\n        Create unlimited slideshows within your Joomla! site. Use the power of MVC tempting to create new slideshow templates or modify the pre-packaged ones. This slide is linked by default to an article, which is part of the default Joomla! sample data.\n      ', 'Powerful, yet simple to use', '', 0, '', '', 2, '', 'title=\ntext=\ntagline=\ncategory=\nauthor=\nreadmore=\ntarget=\nliveData=\n\n'),
(4, 'Powers thousands of sites worldwide', 1, 1, '2012-02-28 19:39:45', '0000-00-00 00:00:00', '2012-02-28 19:39:45', 62, '0000-00-00 00:00:00', 0, 0, 4, 0, 1, '\n        Frontpage Slideshow is used in websites visited by millions of people everyday around the world like Linux.com, BettyConfidential.com, the World Health Organization and many more. This slide is linked by default to a menu item, which is part of the default Joomla! sample data.\n      ', 'From corporate sites to "big league" magazines & portals', '', 0, '', '', 2, '', 'title=\ntext=\ntagline=\ncategory=\nauthor=\nreadmore=\ntarget=\nliveData=\n\n'),
(6, 'HITACHI POWERTOOLS', 2, 1, '2012-03-01 09:06:00', '0000-00-00 00:00:00', '2012-03-01 09:08:21', 62, '2014-08-25 04:53:06', 62, 0, 2, 0, 1, '<p><br mce_bogus="1"></p>', 'HITACHI POWERTOOLS', 'custom', 0, 'http://www.hitachi-powertools.co.th/index.php?option=com_virtuemart&view=category&virtuemart_category_id=4&Itemid=3&lang=en&limitstart=10', '', 2182, '', 'useOriginal=\ntitle=0\ntext=0\ntagline=0\ncategory=0\nauthor=0\nreadmore=0\ntarget=\nliveData=\n\n'),
